LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_FINANCIALSPONSORDATERANGE(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@DATEFROM date = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@DATEFROM = DATEFROM	
	from dbo.SPONSORDATERANGE
	where CONSTITUENTID = @ID
	  and SPONSORTYPECODE = 2
      and (DATETO is null or DATETO >=getdate() )
	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_FINANCIALSPONSORDATERANGE ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @DATEFROM date ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.SPONSORDATERANGE set DATEFROM = @DATEFROM, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where CONSTITUENTID = @ID and SPONSORTYPECODE = 2 and (DATETO is null or DATETO >=@CURRENTDATE ) end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
