SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_DOES_SPONSOR_HAVE_CONSTITUENCY_CODE( @CONSTITUENTID uniqueidentifier, @CONSTITUENCYCODE nvarchar(100) ) as -- get the ID of the given CONSTITUENCYCODE text value: declare @CONSTITUENCYCODEID uniqueidentifier;
select @CONSTITUENCYCODEID = ID from dbo.CONSTITUENCYCODE where LOWER ([DESCRIPTION]) = LOWER(@CONSTITUENCYCODE);
if @CONSTITUENCYCODEID is not null begin -- call the function to see if sponsor has this code or not: 0 is False, 1 is True select dbo.USR_UFN_CONSTITUENT_HAS_CONSTITUENCYCODE (@CONSTITUENTID, @CONSTITUENCYCODEID) as HASCODE end else select 0 as HASCODE
