LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_EFTDECLINEBATCH_SPONSOR(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@CARDHOLDERNAME nvarchar(255) = null output,
	@BATCHDATE date = null output,
	@DECLINEREASON nvarchar(max) = null output,
	@CARDNUMBER nvarchar(4) = null output,
	@CARDEXPIRATION nvarchar(8) = null output,
	@ACCOUNTINFO nvarchar(4000) = null output,
	@BATCHNUMBER nvarchar(100) = null output,
	@CHILDINFO nvarchar(100) = null output,
	@REVENUELOOKUPID nvarchar(100) = null output	
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	SELECT	
			@DATALOADED = 1,
			@TSLONG = USR_EFTDECLINE_BATCH_SPONSORS.TSLONG,
			--USR_EFTDECLINE_BATCH_SPONSORS.ID, 
			@CARDHOLDERNAME = CARDHOLDERNAME, 
			@BATCHDATE = BATCHDATE, 
			@DECLINEREASON = DECLINEREASON, 
			@CARDNUMBER = CARDNUMBER,
			@CARDEXPIRATION = CARDEXPIRATION,
			@ACCOUNTINFO = ACCOUNTINFO,
			@BATCHNUMBER = USR_EFTDECLINE_BATCH_SPONSORS.BATCHNUMBER, 
			@CHILDINFO = CHILDINFO, 
			@REVENUELOOKUPID = REVENUELOOKUPID
			--r.ID as RevenueID
	FROM dbo.USR_EFTDECLINE_BATCH_SPONSORS
	join dbo.REVENUE r on r.LOOKUPID = REVENUELOOKUPID
	where USR_EFTDECLINE_BATCH_SPONSORS.ID = @ID
	-- RevenueID is the key to associating with the Recurring Gift	

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_EFTDECLINEBATCH_SPONSOR ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @CARDHOLDERNAME nvarchar(255), --@BATCHDATE date, @DECLINEREASON nvarchar(max), @CARDNUMBER nvarchar(4), @CARDEXPIRATION nvarchar(8), @ACCOUNTINFO nvarchar(4000) --@BATCHNUMBER nvarchar(100), --@CHILDINFO nvarchar(100), --@REVENUELOOKUPID nvarchar(100) ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_EFTDECLINE_BATCH_SPONSORS set CARDHOLDERNAME = @CARDHOLDERNAME, --@BATCHDATE = BATCHDATE, DECLINEREASON = @DECLINEREASON, CARDNUMBER = @CARDNUMBER, CARDEXPIRATION = @CARDEXPIRATION, ACCOUNTINFO = @ACCOUNTINFO, --@BATCHNUMBER = USR_EFTDECLINE_BATCH_SPONSORS.BATCHNUMBER, --@CHILDINFO = CHILDINFO, --@REVENUELOOKUPID = REVENUELOOKUPID CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
