LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_RECURRINGGIFTEXTENSIONNONSCHEDULE(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@DELAYEFTPAYMENTDUEDATECODEID uniqueidentifier = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@DELAYEFTPAYMENTDUEDATECODEID = DELAYEFTPAYMENTDUEDATECODEID
	from dbo.USR_FINANCIALTRANSACTION_EXTENSION
	where ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_RECURRINGGIFTEXTENSIONNONSCHEDULE ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @DELAYEFTPAYMENTDUEDATECODEID uniqueidentifier ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_FINANCIALTRANSACTION_EXTENSION set DELAYEFTPAYMENTDUEDATECODEID = @DELAYEFTPAYMENTDUEDATECODEID, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
