--declare @HIERARCHYPATH hierarchyid
--select @HIERARCHYPATH = HIERARCHYPATH --from dbo.SPONSORSHIPLOCATION --where ID = @ID;
select so.ID , so.LOOKUPID as 'CHILDID' , dbo.SPONSORSHIPOPPORTUNITYCHILD.[NAME] as 'CHILDNAME' , soLock.LOCKCOUNT , soLock.LOCKED , soLock.SPONSORCOUNT , so.ELIGIBILITY , so.AVAILABILITY , dbo.SPONSORSHIPOPPORTUNITYGROUP.[NAME] as 'OPPORTUNITYNAME' , dbo.SPONSORSHIPLOCATION.LOOKUPID as 'LOCATIONID' from dbo.SPONSORSHIPOPPORTUNITY so inner join dbo.SPONSORSHIPLOCATION on SPONSORSHIPLOCATION.ID = so.SPONSORSHIPLOCATIONID left join dbo.SPONSORSHIPOPPORTUNITYGROUP ON dbo.SPONSORSHIPOPPORTUNITYGROUP.ID = so.SPONSORSHIPOPPORTUNITYGROUPID left join dbo.SPONSORSHIPOPPORTUNITYCHILD ON dbo.SPONSORSHIPOPPORTUNITYCHILD.ID = so.ID right outer join dbo.SPONSORSHIPOPPORTUNITYLOCK soLock on soLock.ID = so.ID --where dbo.SPONSORSHIPOPPORTUNITY.ID in (select ID from dbo.SPONSORSHIPOPPORTUNITYLOCK) --where SPONSORSHIPLOCATION.HIERARCHYPATH.IsDescendantOf(@HIERARCHYPATH) = 1 where LOCKED > 0;
