SQLStoredProcedureSpec.CreateProcedureSQL:
		
create procedure dbo.USR_USP_CREATE_PARTNERSHIP_RELATIONSHIP_WITH_SPONSOR (
	@ID uniqueidentifier = null output,
	@CHANGEAGENTID uniqueidentifier = null,
	@CONSTITUENTID uniqueidentifier,
	@RECIPROCALCONSTITUENTID uniqueidentifier = null,
	@RECIPROCALTYPECODEID uniqueidentifier = null,
	@RELATIONSHIPTYPECODEID uniqueidentifier = null,
	@RELATIONSHIPSTARTDATE datetime,
	@COMMENTS nvarchar(256) = '',
	@PRIMARYSOFTCREDITRELATIONSHIPEXISTS bit = 0,
	@PRIMARYSOFTCREDITMATCHFACTOR decimal(5,2) = 100,
	@RECIPROCALSOFTCREDITRELATIONSHIPEXISTS bit = 0,
	@RECIPROCALSOFTCREDITMATCHFACTOR decimal(5,2) = 100,
	@RECIPROCALRECOGNITIONTYPECODEID uniqueidentifier = null,
	@CURRENTDATE datetime = GETDATE,
	@ISCONTACT bit = 0,
	@ISPRIMARYCONTACT bit = 0,
	@CONTACTTYPECODEID uniqueidentifier = null,
	@RELATIONSHIPENDDATE datetime = null,
	@ISPRIMARYBUSINESS bit = 0,
	@ISMATCHINGGIFTRELATIONSHIP bit = 0
)
as
begin
	-- do work
	 begin try
	
	  if @ID is null
		set @ID = newid();
	
		declare @SETID uniqueidentifier = newid();

		insert into dbo.RELATIONSHIPSET
		(
		  ID,
		  ADDEDBYID,
		  CHANGEDBYID,
		  DATEADDED,
		  DATECHANGED
		)
		values
		(
		  @SETID,
		  @CHANGEAGENTID,
		  @CHANGEAGENTID,
		  @CURRENTDATE,
		  @CURRENTDATE
		);

		insert into dbo.RELATIONSHIP
		(
		  ID,
		  RELATIONSHIPCONSTITUENTID,
		  RECIPROCALCONSTITUENTID,
		  RELATIONSHIPTYPECODEID,
		  RECIPROCALTYPECODEID,
		  ISCONTACT,
		  ISPRIMARYCONTACT,
		  CONTACTTYPECODEID,
		  STARTDATE,
		  ENDDATE,
		  ISPRIMARYBUSINESS,
		  ISMATCHINGGIFTRELATIONSHIP,
		  RELATIONSHIPSETID,
		  COMMENTS,
		  ADDEDBYID,
		  CHANGEDBYID,
		  DATEADDED,
		  DATECHANGED
		)
		values
		(
		  @ID,
		  @CONSTITUENTID,
		  @RECIPROCALCONSTITUENTID,
		  @RELATIONSHIPTYPECODEID,
		  @RECIPROCALTYPECODEID,
		  @ISCONTACT,
		  @ISPRIMARYCONTACT,
		  @CONTACTTYPECODEID,
		  @RELATIONSHIPSTARTDATE,
		  @RELATIONSHIPENDDATE,
		  @ISPRIMARYBUSINESS,
		  @ISMATCHINGGIFTRELATIONSHIP,
		  @SETID,
		  @COMMENTS,
		  @CHANGEAGENTID,
		  @CHANGEAGENTID,
		  @CURRENTDATE,
		  @CURRENTDATE
		);
	
		return 0;
	
	end try
	
	begin catch
		exec dbo.USP_RAISE_ERROR;
		return 1;
	end catch

end
