SQLStoredProcedureSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_SET_ISMOSTRECENTCOMMITMENTFLAG_FOR_SPONSORSHIP ( @sponsorShipId uniqueidentifier, -- sponsorship id of the 'current' commitment @CHANGEAGENTID uniqueidentifier ) as begin begin try if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
-- this is obtained from the sponsorshpId passed in: declare @SPONSORSHIPCOMMITMENTID uniqueidentifier;
select @SPONSORSHIPCOMMITMENTID = SPONSORSHIPCOMMITMENTID from dbo.SPONSORSHIP where ID = @sponsorShipId
if @SPONSORSHIPCOMMITMENTID is null RAISERROR ('Unable to find Sponsorship Commitment for this Sponsorship!', 16, 1)
-- get the 'active' row and ensure the ismostrecentcommitment is set to 1 declare @currentMostRecentValue bit; select @currentMostRecentValue = ISMOSTRECENTFORCOMMITMENT from dbo.SPONSORSHIP where ID = @sponsorShipId
if @currentMostRecentValue = 0 begin -- update flag to 1 update dbo.SPONSORSHIP set ISMOSTRECENTFORCOMMITMENT = 1 where ID = @sponsorShipId; -- @activeRowId; end
-- update the remaining commitment line rows, setting the most recent commitment flags to 0 update dbo.SPONSORSHIP set ISMOSTRECENTFORCOMMITMENT = 0, CHANGEDBYID = @CHANGEAGENTID where ID <> @sponsorShipId and SPONSORSHIPCOMMITMENTID = @SPONSORSHIPCOMMITMENTID
return 0; end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
end
