SQLFunctionSpec.CreateFunctionSQL:
		
create function dbo.USR_UFN_CHECK_RECURRINGGIFT_ISHELD(
	@REVENUESPLITID uniqueidentifier
)
returns bit
with execute as caller
as begin
	-- do work here and return a value
	declare @isHeld bit = 0;  -- default to No
	if exists (select 1
		from dbo.USR_FINANCIALTRANSACTION_EXTENSION
		where (ID = (select REVENUEID from dbo.REVENUESPLIT where REVENUEID = @REVENUESPLITID)
		and ISEFTDECLINED = 1)
		)
		set @isHeld = 1;
		
	return @isHeld;
end
