SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_GETFINANCIALTRANSACTIONID_FORSPONSORSHIP( @SPONSORSHIPID uniqueidentifier ) returns uniqueidentifier with execute as caller as begin declare @financialTransactionId uniqueidentifier;
select @financialTransactionId = r.ID from dbo.SPONSORSHIP s join dbo.REVENUESPLIT rs on rs.ID = s.REVENUESPLITID join dbo.REVENUE r on r.ID = rs.REVENUEID where s.ID = @sponsorshipId
return @financialTransactionId; end
