SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_GET_PAYMENTMETHODCODE_BY_REVENUESPLITID( @REVENUESPLITID uniqueidentifier ) returns tinyint with execute as caller as begin -- do work here and return a value declare @PAYMENTMETHODCODE tinyint;
select @PAYMENTMETHODCODE = PAYMENTMETHODCODE from dbo.REVENUEPAYMENTMETHOD rp join dbo.REVENUESPLIT revsplit on revsplit.REVENUEID = rp.REVENUEID where revsplit.ID = @REVENUESPLITID
return @PAYMENTMETHODCODE end
