SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_GET_SPONSORSHIPEXTENSION_CLH_INFO (@sponsorshipId uniqueidentifier) RETURNS @DelinquencyCampaignResults table ( [INITIALDESCRIPTION] nvarchar(100) NULL, [CURRENTDESCRIPTION] nvarchar(100) NULL, [CONVERSIONDATE] datetime NULL, [DAYSTOCONVERT] int NULL ) with execute as caller as begin BEGIN INSERT @DelinquencyCampaignResults select initial.[DESCRIPTION], [current].[DESCRIPTION], CONVERSIONDATE, DAYSTOCONVERT from dbo.USR_SPONSORSHIPEXTENSION left join dbo.USR_INITIALOPPORTUNITYSPONSORSHIPSTATUSCODE initial on initial.ID = INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID left join dbo.USR_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE [current] on [current].ID = CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID where USR_SPONSORSHIPEXTENSION.ID = @sponsorshipId; END;
RETURN;
end
