SPSearchList.common:CreateProcedureSQL:
			
create procedure dbo.USP_SEARCHLIST_PARTNERFORMERCYORG
(
	@NAME nvarchar(100) = null,
	@LOOKUPID nvarchar(10) = null,
	@CITY nvarchar(100) = null,
	@STATEID uniqueidentifier = null,
	@MAXROWS smallint = 500
)
as
	set @NAME = '%' + coalesce(@NAME,'') + '%' ;
	set @NAME = replace(@NAME,'*','%');
	set @LOOKUPID = coalesce(@LOOKUPID,'') + '%' ;
	set @LOOKUPID = replace(@LOOKUPID,'*','%');
	set @CITY = '%' + coalesce(@CITY,'') + '%' ;
	set @CITY = replace(@CITY,'*','%');
	
	declare @partnerConstituentCodeID uniqueidentifier
	exec dbo.USR_USP_GETCODETABLEITEMID_DYNAMIC @partnerConstituentCodeID output, 'Partner for Mercy', 'CONSTITUENCYCODE', 1, ''
	
	select	top(@MAXROWS)
			org.ID,
			org.LOOKUPID, 
			org.NAME, 
			addr.CITY, 
			st.DESCRIPTION as STATENAME
			--st.ABBREVIATION as STATEABBREVIATION
				
	  from	dbo.CONSTITUENT org
 left join	dbo.ADDRESS addr on 
				org.ID = addr.CONSTITUENTID
 left join	dbo.STATE st on 
				st.ID = addr.STATEID
				
	where org.ISORGANIZATION = 1 
	  and exists (select CONSTITUENTID from dbo.CONSTITUENCY where CONSTITUENCYCODEID = @partnerConstituentCodeID and CONSTITUENTID = org.ID)
	  and (org.NAME LIKE @NAME)
	  and (org.LOOKUPID LIKE @LOOKUPID)
	  and (addr.CITY like @CITY)
	  and (@STATEID is null or @STATEID = addr.STATEID)
	order by 
		NAME asc
