LoadImplementation.common:CreateProcedureSQL:
				
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_SPONSORTERMINATEPROCESS(
	@ID uniqueidentifier,
	@DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,	
    @NAME nvarchar(100) = NULL output,
	@DESCRIPTION nvarchar(255) = NULL output,
	@IDSETREGISTERID uniqueidentifier = NULL output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select	@DATALOADED       = 1,
			@TSLONG			  = TSLONG,
			@NAME             = NAME,
			@DESCRIPTION      =	DESCRIPTION,
			@IDSETREGISTERID  = IDSETREGISTERID
			
	  from  dbo.USR_SPONSORTERMINATE_BUSINESSPROCESS
	  
	 where  ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_SPONSORTERMINATEPROCESS ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @NAME nvarchar(100), @DESCRIPTION nvarchar(255), @IDSETREGISTERID uniqueidentifier ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_SPONSORTERMINATE_BUSINESSPROCESS set NAME = @NAME, DESCRIPTION = @DESCRIPTION, IDSETREGISTERID = @IDSETREGISTERID, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
