create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_SPONSORSHIPLOCATIONEXTENSION( @ID uniqueidentifier, @DATALOADED bit = 0 output, @TSLONG bigint = 0 output, @REPORTRECIPIENTID uniqueidentifier = null output ) as
set nocount on;
-- be sure to set these, in case the select returns no rows set @DATALOADED = 0 set @TSLONG = 0
-- if not exists (select ID from dbo.USR_SPONSORSHIPLOCATIONEXTENTION where SPONSORSHIPLOCATIONID = @ID) -- begin -- declare @CHANGEAGENTID uniqueidentifier -- exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
-- Insert into USR_SPONSORSHIPLOCATIONEXTENTION -- (SponsorshiplocationID, REPORTRECIPIENTID, addedbyid, changedbyid) -- values -- (@ID, '', @CHANGEAGENTID, @CHANGEAGENTID) -- end -- populate the output parameters, which correspond to fields on the form. Note that -- we set @DATALOADED = 1 to indicate that the load was successful. Otherwise, the system -- will display a "no data loaded" message. Also note that we fetch the TSLONG so that concurrency -- can be considered. begin try if not exists (select ID from dbo.USR_SPONSORSHIPLOCATIONEXTENTION where SPONSORSHIPLOCATIONID = @ID) begin set @DATALOADED = 1 set @TSLONG = 0 set @REPORTRECIPIENTID = null end else select @DATALOADED = 1, @TSLONG = TSLONG, @REPORTRECIPIENTID = REPORTRECIPIENTID from dbo.USR_SPONSORSHIPLOCATIONEXTENTION where SPONSORSHIPLOCATIONID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
