LoadImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_SPONSORSHIPEXTENSION_PRELOAD ( @SPONSORSHIPID uniqueidentifier, @SPONSORSHIPOPPORTUNITYID uniqueidentifier = null output ) as set nocount on;
begin try select @SPONSORSHIPOPPORTUNITYID = SPONSORSHIPOPPORTUNITYID from dbo.sponsorship where ID = @SPONSORSHIPID end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_SPONSORSHIPEXTENSION ( @ID uniqueidentifier = null output, @SPONSORSHIPID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier = null, @CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier = null, @ISCURRENT bit = null, @SPONSORSHIPOPPORTUNITYID uniqueidentifier ) as
set nocount on;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
--declare @CURRENTDATE datetime --set @CURRENTDATE = getdate()
declare @DATEADDED datetime = null; declare @DATECHANGED datetime = null;
begin try -- Memphis 8/20/14: below is the content of the USR_USP_SPONSORSHIPEXTENSION_INSERT sproc, added so this is now self-contained:
-- strip out the time for the dateadded if @DATEADDED is null set @DATEADDED = DATEADD(dd, 0, DATEDIFF(dd, 0, getdate()));
if @DATECHANGED is null set @DATECHANGED = getdate();
-- set the inserted row to be the current row for this opportunity: --declare @ISCURRENT bit = 1;
--10/22/12 Memphis add a transaction so there's no updating if the insert fails for some reason! begin transaction updateInsert;
-- check for the existence of this sponsorship row already, as it may have already been inserted through some other process declare @SponExId uniqueidentifier; select @SponExId = ID from dbo.usr_sponsorshipextension where ID = @SPONSORSHIPID;
-- if we have an ID, then the Sponsorship row already exists, so do nothing if @SponExId is null begin -- before inserting, set all rows to be not current but only if user is setting this one to Current! if @ISCURRENT = 1 begin update dbo.USR_SPONSORSHIPEXTENSION set ISCURRENT = 0 where SPONSORSHIPOPPORTUNITYID = @SPONSORSHIPOPPORTUNITYID end
INSERT INTO [dbo].[USR_SPONSORSHIPEXTENSION] ( [ID] ,[INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID] ,[CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID] ,[CONVERSIONDATE] ,[SPONSORSHIPOPPORTUNITYID] ,[DAYSTOCONVERT] ,[ISCURRENT] ,[ADDEDBYID] ,[CHANGEDBYID] ,[DATEADDED] ,[DATECHANGED] ) VALUES ( @SPONSORSHIPID ,@INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID ,@CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID ,NULL ,@SPONSORSHIPOPPORTUNITYID ,0 ,@ISCURRENT ,@CHANGEAGENTID ,@CHANGEAGENTID ,@DATEADDED ,@DATECHANGED ) end
commit transaction updateInsert;
end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
