LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_SPONSORSHIPEXTENSION(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier = null output,
	@CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier = null output,
	@CONVERSIONDATE date = null output,
	@DAYSTOCONVERT int = null output,
	@ISCURRENT bit = null output,
	@ISACCOUNTCORRECTION bit = null output,
	@SPONSORSHIPOPPORTUNITYID uniqueidentifier = null output,
	@DATEADDED datetime = null output,
	@ADDEDBYNAME nvarchar(128) = null output,
	@CHANGEDBYNAME nvarchar(128) = null output,
	@STATUSMESSAGE nvarchar(100) = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID = INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID, 
		@CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID = CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID,
		@CONVERSIONDATE = CONVERSIONDATE,
		@DAYSTOCONVERT = DAYSTOCONVERT,
		@ISCURRENT = ISCURRENT,
		@ISACCOUNTCORRECTION = ISACCOUNTCORRECTION,
		@SPONSORSHIPOPPORTUNITYID = SPONSORSHIPOPPORTUNITYID,
		@DATEADDED = dbo.USR_SPONSORSHIPEXTENSION.DATEADDED,
		@ADDEDBYNAME = ADDEDBY.USERNAME,
		@CHANGEDBYNAME = CHANGEDBY.USERNAME
	from dbo.USR_SPONSORSHIPEXTENSION
	left join dbo.CHANGEAGENT ADDEDBY on ADDEDBY.ID = dbo.USR_SPONSORSHIPEXTENSION.ADDEDBYID
	left join dbo.CHANGEAGENT CHANGEDBY on CHANGEDBY.ID = dbo.USR_SPONSORSHIPEXTENSION.CHANGEDBYID 
	where dbo.USR_SPONSORSHIPEXTENSION.ID = @ID

	if @ISCURRENT = 0
		set @STATUSMESSAGE = 'This is NOT the current row for this Child Sponsorship!';
	else
		set @STATUSMESSAGE = 'This IS the current row for this Child Sponsorship!';
		
	-- check for conditions where no iscurrent rows exist or more than rows exist:
	if exists (select count(*), ISCURRENT
				from dbo.USR_SPONSORSHIPEXTENSION
				where (ISCURRENT = 0
				and SPONSORSHIPOPPORTUNITYID = @SPONSORSHIPOPPORTUNITYID)
				group by ISCURRENT
				having count(*) > 1)
		begin
			set @STATUSMESSAGE = 'Child has NO IsCurrent rows set!';
		end
	
	if exists (select count(*), ISCURRENT
				from dbo.USR_SPONSORSHIPEXTENSION
				where (ISCURRENT = 1
				and SPONSORSHIPOPPORTUNITYID = @SPONSORSHIPOPPORTUNITYID)
				group by ISCURRENT
				having count(*) > 1)
		begin
			set @STATUSMESSAGE = 'Child has MORE THAN ONE IsCurrent rows set!';
		end
	

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_SPONSORSHIPEXTENSION( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier, @CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID uniqueidentifier, @CONVERSIONDATE date, @DAYSTOCONVERT int, @ISCURRENT bit, @ISACCOUNTCORRECTION bit, @SPONSORSHIPOPPORTUNITYID uniqueidentifier ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- calculate DaysToConvert if there is a Conversion Date value: -- USR_UFN_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE_GETID_FORDESCRIPTION() -- also calculate the DaysToConvert value upon save: -- -Value = difference between Start Date of the Sponsorship & the Conversion Date -- -When these rules exist: -- - Current Status must be Sponsored or Formerly Sponsored, then the Conversion Date must be filled in -- - and then calculate the Days to Convert value
declare @sponsoredStatusId uniqueidentifier; declare @formerlySponsoredStatusId uniqueidentifier;
--get the Current Status ID values of Sponsored and Formerly Sponsored to compare to what's being passed in from the Form: set @sponsoredStatusId = dbo.USR_UFN_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE_GETID_FORDESCRIPTION('Sponsored'); set @formerlySponsoredStatusId = dbo.USR_UFN_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE_GETID_FORDESCRIPTION('Formerly Sponsored');
-- check if the current status id value is either Sponsored or Formerly Sponsored: if (@CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID = @sponsoredStatusId or @CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID = @formerlySponsoredStatusId) begin -- @CONVERSIONDATE is filled in: if @CONVERSIONDATE is not null begin -- @DAYSTOCONVERT is not yet set if (@DAYSTOCONVERT is null or (@DAYSTOCONVERT is not null and @DAYSTOCONVERT = 0)) begin -- need to get the Sponsorship start date to use for calculation declare @startDate date; select @startDate = STARTDATE from dbo.SPONSORSHIP where ID = @ID;
if @startDate is not null begin -- calculate the DAYSTOCONVERT value: set @DAYSTOCONVERT = datediff(dd, @startDate, @CONVERSIONDATE) end end end end
-- handle updating the data update dbo.USR_SPONSORSHIPEXTENSION set INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID = @INITIALOPPORTUNITYSPONSORSHIPSTATUSCODEID, CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID = @CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID, DAYSTOCONVERT = @DAYSTOCONVERT, CONVERSIONDATE = @CONVERSIONDATE, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE, ISCURRENT = @ISCURRENT, ISACCOUNTCORRECTION = @ISACCOUNTCORRECTION where ID = @ID
-- if the user is setting the iscurrent flag, or it has already been set, then ensure this is the ONLY current record for this sponsorship, -- set all other rows for this child to 0 (false). if @ISCURRENT = 1 begin update dbo.USR_SPONSORSHIPEXTENSION set ISCURRENT = 0 where (SPONSORSHIPOPPORTUNITYID = @SPONSORSHIPOPPORTUNITYID and ID <> @ID) end
end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
